import java.util.*;
class P90
{
	public static void main(String args[])
	{
		Scanner s1=new Scanner(System.in);
		int n;
		double sum=0;
		System.out.print("Enter number :");
		n=s1.nextInt();

		for(int i=1;i<=n;i++)
		{
			sum=sum+1.0/i;
		}
		System.out.println("Sum of series is:"+sum);
	}
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       